// Wishlist.jsx
import React, { useState } from 'react';
import { VStack, Heading, Text, Button } from '@chakra-ui/react';
import ShareCard from './ShareCard';

const Wishlist = () => {
    const [wishlist, setWishlist] = useState([]);

    const addToWishlist = (share) => {
        if (!wishlist.find((item) => item.id === share.id)) {
            setWishlist([...wishlist, { ...share, additionalDetails: true }]);
        }
    };

    const removeFromWishlist = (id) => {
        setWishlist(wishlist.filter((item) => item.id !== id));
    };

    return (
        <VStack spacing={4} align="start" p={8}>
            <Heading size="xl" mb={4}>
                My Wishlist
            </Heading>
            {wishlist.length === 0 ? (
                <Text>Your wishlist is empty.</Text>
            ) : (
                wishlist.map((item) => (
                    <div key={item.id}>
                        <ShareCard {...item} addToWishlist={addToWishlist} />
                        <Button colorScheme="red" onClick={() => removeFromWishlist(item.id)}>
                            Remove
                        </Button>
                        {item.additionalDetails && (
                            <div>
                                <VStack spacing={4} p={4} alignItems="flex-start">
                                    <Heading size="lg">{item.name}</Heading>
                                    {/* Display additional details or remove this section if not needed */}
                                    <Text>Max Supply: {item.market_data.max_supply}</Text>
                                    <Text>Circulating Supply: {item.market_data.circulating_supply}</Text>
                                    <Text>Market Cap: ₹ {item.market_data.market_cap.inr}</Text>
                                    <Text>All Time Low: ₹ {item.market_data.atl.inr}</Text>
                                    <Text>All Time High: ₹ {item.market_data.ath.inr}</Text>
                                </VStack>
                            </div>
                        )}
                    </div>
                ))
            )}
        </VStack>
    );
};

export default Wishlist;
